const fetch = require('node-fetch');
module.exports.authVehicle = async function V(carYear, carMake, carModel, apiKey){

    let opt={
    headers: {
    'Host':'marketcheck-prod.apigee.net',
    'Content-Type':'application/json'
    }
}
let fetching = await fetch(`http://api.marketcheck.com/v1/search?api_key=${apiKey}&year=${carYear}&make=${carMake}&model=${carModel}`, opt)
    let response=fetching.json()
    return await response
}
